
package _4day;

public class charAscii_reverse {

	public static void main(String[] args) {
		
		for(int i =65;i<=69;i++) {
			
			for(int j=i;j>=65;j--) {
				System.out.print(" "+(char)j);
			
			}System.out.println();
		}
		
		//System.out.print(" "+(char)a);
		// TODO Auto-generated method stub

	}

}
